(`Id`, `Code`, `Type`, `Name`, `IconUrl`, `Authority`, `ResponseType`, `ClientId`, `ClientSecretEncrypted`, `Scope`, `FetchUserInfo`, `Pkce`, `Disabled`, `Trusted`)
VALUES
(@Id, @Code, @Type, @Name, @IconUrl, @Authority, @ResponseType, @ClientId, @ClientSecretEncrypted, @Scope, @FetchUserInfo, @Pkce, @Disabled, @Trusted);

SELECT *
FROM `idp_oidc_providers` WHERE `Id` = @Id;

