SqlOperation.TextCommand:
SELECT * FROM `job_jobs`
WHERE `WorkspaceId` = @WorkspaceId
ORDER BY `LocalId` Asc;

SET @nextLocalId = (
  SELECT COALESCE(MAX(LocalId), 0) + 1
  FROM Jobs
  WHERE `WorkspaceId` = @WorkspaceId
);

INSERT INTO `job_jobs`
(`Id`, `WorkspaceId`, `LocalId`, `ClusterId`, `UserId`, `Scope`, `Manifest`, `TotalTasks`, 
`CompletedTasks`, `Status`, `Message`, `PendingAt`, `RunningAt`, `CompletedAt`)
VALUES
(@Id, @WorkspaceId, @nextLocalId, @ClusterId, @UserId, @Scope, @Manifest, @TotalTasks,
@CompletedTasks, @Status, @Message, @PendingAt, @RunningAt, @CompletedAt);

SELECT * FROM `job_jobs`
WHERE `WorkspaceId` = @WorkspaceId AND `Id` = @Id;
